import React from 'react';
import { connect } from 'react-redux';

import './CollectionItem.Styles.scss';
import CustomButton from '../CustomButton/CustomButton';
import { addItem } from '../../Redux/Cart/CartAction';
import { CollectionItemContainer, CollectionItemImageContainer, AddButton, CollectionFooterContainer, NameContainer } from './CollectionItem.Styles'; 

const CollectionItem = ({ item, addItem }) => {
    const { name, price, imageUrl} = item
    return(
    <CollectionItemContainer>
        <CollectionItemImageContainer className='image' imageUrl={imageUrl} />
        <CollectionFooterContainer>
            <NameContainer>{name}</NameContainer>
            <span className='price'>${price}</span>
        </CollectionFooterContainer>
        <AddButton onClick={() => addItem(item)} inverted>Add tocart</AddButton>
    </CollectionItemContainer>
    )
}

const mapDispatchToProps = dispatch =>({
    addItem: item => dispatch(addItem(item))
})

export default connect(null, mapDispatchToProps)(CollectionItem);
